import React, { Component } from 'react';

import Select from './Select';

class Book extends Component {

    constructor(props) {
      super(props);

      this.state = {
        info: props.info
      }
    }

    componentDidUpdate() {
        this.props.updateList()
    }

    render() {
        return (
            <div className="book">
              <div className="book-top">
                <div className="book-cover" style={{ width: 128, height: 193, backgroundImage: `url(${this.state.info.imageLinks.thumbnail})` }}></div>
                <div className="book-shelf-changer">
                  <select>
                    <option value="move" disabled>Move to...</option>
                    <option value="currentlyReading">Currently Reading</option>
                    <option value="wantToRead">Want to Read</option>
                    <option value="read">Read</option>
                    <option value="none">None</option>
                  </select>
                </div>
              </div>
              <div className="book-title">{this.state.info.title}</div>
              <div className="book-authors">{this.state.info.author}</div>
            </div>
        )
    }
}


export default Book;
